import {vi} from "vitest";

// use like const {SidebarView, noSidebarView} = await getModule("/src/views/sidebarView")
// if the module is not found, noSidebarView will be set to the error
export async function getModule(m){
  try{
    return await import(m);
  }catch(e){
    const index= m.lastIndexOf("/");
     return {["no"+m.substring(index+1, index+2).toUpperCase()+m.substring(index+2)]:e};
  }
}
