import {vi} from "vitest";

import {createElement, Fragment} from "react";
import {h} from "vue";

import {render as vue_render} from "@testing-library/vue";
import {render as react_render}  from "@testing-library/react";

export function reactRender(component, props){
    window.React={createElement, Fragment};
    return react_render(createElement(component, props));
}

export function vueRender(component, props, global={}){
    window.React={createElement:h};
    return vue_render({
        components: { Component: component},
        template: "<Component/>"
    },{
        props,
	global
    });
}
