import { vi, describe, it, expect, afterEach} from "vitest";

vi.mock("mobx-react-lite");
import {observer} from "mobx-react-lite";
observer.mockImplementation(function(x){ return x; }) ;

import {webCustomEvents} from "./webCustomEvents";
import {reactRender, vueRender} from "./render";
import { runCustomEventTests } from "./tw1.5-10.handleCustomEvents";

// re-run these tests if anything happens to /*js/sidebarPresenter or to web sidebarView
import.meta.glob("/src/vuejs/sidebarPresenter.*sx")
import.meta.glob("/src/reactjs/sidebarPresenter.*sx")
import.meta.glob("/src/views/sidebarView.*sx")

describe.for([
    {   name:"React",   render:     reactRender, path:"reactjs"},
    {   name:"Vue",     render:     vueRender, path:"vuejs"},
])("", async function(framework){
    runCustomEventTests(framework, webCustomEvents);
});
